mapper.insert:
INSERT INTO tbUser(
	username 
	, password 
	, firstName 
	, lastName 
	, enabled 
	, regId
	, regIp 
	, regDate
	, delYn
)
VALUE(
	#{username}
	, #{password}
	, #{firstName}
	, #{lastName}
	, 'N'
	, #{regId}
	, #{regIp}
	, NOW()
	, 'N'
)

mapper.update:
UPDATE tbUser SET 
	firstName = #{firstName}
	, lastName = #{lastName}
	, modId = #{modId}
	, modIp	= #{modIp}
	, modDate = NOW()
WHERE username = #{username}

mapper.insert:
INSERT INTO tbUserRole(
